import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-Eventos',
  templateUrl: './Eventos.component.html',
  styleUrls: ['./Eventos.component.scss']
})
export class EventosComponent implements OnInit {

  public eventos: any;
  constructor(private http:HttpClient) { }

  ngOnInit():void {

    this.getEventos();
  }
   public getEventos():void{
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => this.eventos = response,
      error => console.log(error)
    );


   }
}
<h2>Eventos</h2>
<hr>
<form action="" class="d-flex flex-row align-items-center flex-wrap ">
  <div class="d-flex mb-3" >
    <input type="text" class="form-control me-2" placeholder="buscar">
    <button class="btn btn-outline-success">Buscar</button>
  </div>
</form>
<table class="table table-striped ">
  <thead class="table-dark">
<tr>
      <th>#</th>
      <th>Imagem</th>
      <th>Tema</th>
      <th>Local</th>
      <th>Data</th>
      <th>Qtd Pessoas</th>
      <th>Lote</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let f of [10].constructor(numRows); let r = index">
      <td class="gridCell" *ngFor="let col of [].constructor(numCols); let c = index">
        {{gridCards[r][c].name}}
      </td>
    </tr>

  </tbody>
</table>
